import React from "react";
import QuestionPopUp from "./components/questionPopUp";

export default class App extends React.Component {

  constructor() {
    super();
    this.state = {
      score: 0,
      isOpenPopUp: false,
      zIndex: 1,
      ques: [
        ["#fff", "#000"],
        ["#fff", "#000"],
        ["#fff", "#000"],
        ["#fff", "#000"],
        ["#fff", "#000"],
        ["#fff", "#000"],
      ]
    }
  }

  openPopUp = (option) => {
    this.setState({
      option: option,
      isOpenPopUp: true,
      zIndex: -1
    })
  }

  closePopUp() {
    this.setState({
      isOpenPopUp: false,
      zIndex: 1
    })
  }

  setStatus = (status) => {
    let tempArray = [];
    for(let index = 0; index < this.state.ques.length; index++){
      if(index == thoption){
        tempArray[option][0] = "green";
        tempArray[option][1] = "#fff";
      }
      else{
        tempArray[option] = this.state.ques[option];
      }
    }

    this.setState({
      ques: tempArray,
    })
  }

  render() {
    return (
      <>
        <div id="wrap-page">

          <img id="background" src="./assets/images/interface/background_dever.png" loading="lazy" alt="background" />

          <div id="navigation">
            <div id="wrap-logo-dever">
              <a href="https://www.facebook.com/FPTUDever">
                <img id="logo_dever" src="./assets/images/interface/logo_dever_light.png" loading="lazy" alt="logo dever" />
              </a>
            </div>
            <img id="logo_fpt" src="./assets/images/interface/logo_fpt.png" loading="lazy" alt="logo fpt university" />
          </div>

          <p id="score">Score: {this.state.score}</p>

          <div id='wrap-select-level' style={{ zIndex: `${this.state.zIndex}` }}>
            <h3>Select level</h3>
            <div id="levels">
              <div className="level" style={{ backgroundColor: this.state.ques[0][0] }}>
                <div onClick={() => this.openPopUp(0)} className="decor-level" >Level 1</div>
              </div>
              <div className="level" style={{ backgroundColor: this.state.ques[1][0] }}>
                <div onClick={() => this.openPopUp(1)} className="decor-level" >Level 2</div>
              </div>
              <div className="level" style={{ backgroundColor: this.state.ques[2][0] }}>
                <div onClick={() => this.openPopUp(2)} className="decor-level" >Level 3</div>
              </div>
              <div className="level" style={{ backgroundColor: this.state.ques[3][0] }}>
                <div onClick={() => this.openPopUp(3)} className="decor-level" >Level 4</div>
              </div>
              <div className="level" style={{ backgroundColor: this.state.ques[4][0] }}>
                <div onClick={() => this.openPopUp(4)} className="decor-level" >Level 5</div>
              </div>
              <div className="level" style={{ backgroundColor: this.state.ques[5][0] }}>
                <div onClick={() => this.openPopUp(5)} className="decor-level" >Level 6</div>
              </div>
            </div>
          </div>

          {
            (this.state.isOpenPopUp) &&
            <QuestionPopUp setResult={(status) => this.setStatus(status)} level={this.state.option} setQues={Math.floor(Math.random() * 4)} closeFunc={() => this.closePopUp()} />
          }

        </div>
      </>
    )
  }
}
