import React from "react";

const answer = [
    [
        [['A, Int a = 3;', 'B, int = 3;', 'C, int a = 3;', 'D, int a = 3.0;'], "How to declare an integer variable in C++?"],
        [['A, Use <!-- -->', 'B, Use /* */', 'C, Use #', 'D, Use **'], "How to comment in C++?"],
        [['A, System.out.println("Hello World")', 'B, print("Hello World")', 'C, cout<<"Hello world"', 'D, printf("Hello World")'], "What's correct format to print string to screen?"],
        [['A, #include<stream>', 'B, #include<iostream>', 'C, #include<io.h>', 'D, #include<stdlib.h>'], "Which header file lets us work with input and output objects?"],
    ],
    [
        [['A, 3', 'B, 2', 'C, 1', 'D, 0'], "What's output of the program?"],
        [['A, int sum == 0', 'B, No error', 'C, ...', 'D, ...'], "What's the error of the code above?"],
        [['A, 1 2', 'B, 1 1', 'C, 2 2', 'D, 2 1'], "What's value of a, b after code above execute?"],
        [['A, Error', 'B, 3', 'C, 2', 'D, 4'], "What's output of the program?"],
    ],
    [
        [['A, 0', 'B, 1', 'C, 2', 'D, Error'], "What's output of the program?"],
        [['A, ...', 'B, ...', 'C, ...', 'D, ...'], "What's condition sho"],
        [['A, ...', 'B, ...', 'C, ...', 'D, ...'], ""],
        [['A, ...', 'B, ...', 'C, ...', 'D, ...'], ""],
    ],
    [
        [['A, int a = a == 1', 'B, int a + b = 1', 'C, int b = a ** 2', 'D, int -a = 1'], "What's the valid code?"],
        [['A, int func();', 'B, void func();', 'C, char func();', 'D, float func();'], "What's the function can't return a value?"],
        [['A, ...', 'B, ...', 'C, ...', 'D, ...'], "What's the valid code?"],
        [['A, ...', 'B, ...', 'C, ...', 'D, ...'], "What's the valid code?"],
    ],
    [
        [['A, Hello', 'B, Hello Hi', 'C, HelloHi', 'D, Hi'], "What's output of the program?"],
        [['A, 5 9', 'B, 9 9', 'C, 5 5', 'D, 9 5'], "What's output of the program?"],
        [['A, Condition is true', 'B, Condition is false', 'C, Compiler error', 'D, Runtime error'], "What's the error of the code above?"],
        [['A, ...', 'B, ...', 'C, ...', 'D, ...'], ""],
    ],
    [
        [['A, Yêu', 'B, Rất yêu', 'C, Rất rất yêu', 'D, Không hề'], "Bạn có yêu FPT không?"],
        [['A, Yêu', 'B, Rất yêu', 'C, Rất rất yêu', 'D, Không hề'], "Bạn có yêu FPT không?"],
        [['A, Yêu', 'B, Rất yêu', 'C, Rất rất yêu', 'D, Không hề'], "Bạn có yêu FPT không?"],
        [['A, Yêu', 'B, Rất yêu', 'C, Rất rất yêu', 'D, Không hề'], "Bạn có yêu FPT không?"],
    ],
]

export default class QuestionPopUp extends React.Component {

    render() {
        let sourse = `./assets/images/error/${[this.props.level]}.${this.props.setQues}.png`;
        return (
            <>
                <div id="wrap-pop-up">
                    {
                        (this.props.level !== 0 && this.props.level !== 3) && 
                        <img className="question" loading="lazy" alt="question" src={sourse} />
                    }
                    <h3 style={{color: "#fff"}}>{answer[this.props.level][this.props.setQues][1]}</h3>
                    <div id="wrap-answer">
                        {
                            answer[this.props.level][this.props.setQues][0].map((option, key) =>
                                <div onClick={this.props.closeFunc} className="option" key={key}>{option}</div>)
                        }
                    </div>
                </div>

            </>
        );
    }
}
